import React, { useState, useEffect } from 'react';
import './Timer.css';

const CountdownTimer = ({ initialCount = 60 }) => {
  const [count, setCount] = useState(initialCount);

  useEffect(() => {
    // Exit early when we reach 0
    if (count <= 0) {
      console.log('Timer finished');
      // Here you can trigger any action after the countdown finishes
      return;
    }

    // Save intervalId to clear the interval when the
    // component re-renders or unmounts
    const intervalId = setInterval(() => {
      setCount(count - 1);
    }, 1000);

    // Clear interval on re-render to avoid memory leaks
    return () => clearInterval(intervalId);
    // Add count as a dependency to re-run the effect when it changes
  }, [count]);

  return (
    <div className="clock-display">
     {count}
     <button onClick={() => setCount(initialCount)}> Reset Timer </button>
    </div>


  );
};

export default CountdownTimer;
